mapper.update:
UPDATE product
prod_name = #{prodName} , prod_detail = #{prodDetail} , manufacture_day = TO_DATE(#{manuDate}, 'yy/mm/dd') , price = #{price} , image_file = #{fileName}
WHERE prod_no = #{prodNo}

mapper.select:
  	SELECT *
  	FROM (	SELECT inner_table.* , ROWNUM AS row_seq
  					FROM		(	SELECT p.prod_no, p.prod_name, p.prod_detail, 
  									TO_DATE(p.manufacture_day,'yy/mm/dd'), p.price, p.image_file, 
  									p.reg_date, t.tran_status_code
										FROM product p, transaction t 


													p.prod_no = t.prod_no(+)

									 				AND p.prod_no = #{searchKeyword}


									 				AND p.prod_name = #{searchKeyword}


									 				AND p.price = #{searchKeyword}



										ORDER BY prod_no ) inner_table
					WHERE ROWNUM <= #{endRowNum} )
	WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

  	SELECT COUNT(*)
  	FROM(	SELECT *
					FROM product



					 		prod_no = #{searchKeyword}


					 		prod_name = #{searchKeyword}


					 		price = #{searchKeyword}


					 ) countTable
